INSERT INTO `products` (`id`, `name`, `category_id`, `image_url`, `price`, `slug`, `content`, `description`, `quantity`, `created_at`, `updated_at`) VALUES (3, 'Rau mồng tơi Organic 300gr', 12, 'assets/uploads/68ab22aa29136.png', 26000, 'rau-mong-toi-organic-300gr', '<p><span helvetica=\"\" neue=\"\" style=\"color: rgb(137, 137, 137); font-family: Arial, \">Rau chứa nhiều sắt, canxi, vitamin A, C v&agrave; c&aacute;c vitamin nh&oacute;m B. Mồng tơi c&oacute; vị ngọt, gi&ograve;n v&agrave; hơi nhớt đặc trưng Thường được d&ugrave;ng để nấu canh t&ocirc;m, nấu với mướp, nấu với rau đay hay luộc chấm muối vừng. Rau Mồng Tơi Hữu Cơ Vinamit (300G)&nbsp;l&agrave; loại rau c&oacute; vị ngọt, gi&ograve;n v&agrave; hơi nhớt đặc trưng. Mồng tơ</span></p>\r\n', '', NULL, '2025-08-24 21:33:14', '2025-08-24 21:33:32');
INSERT INTO `products` (`id`, `name`, `category_id`, `image_url`, `price`, `slug`, `content`, `description`, `quantity`, `created_at`, `updated_at`) VALUES (4, 'Cải thảo dún Organic 300gr', 12, 'assets/uploads/68ab22dc9e632.png', 24000, 'cai-thao-dun-organic-300gr', '<p><span style=\"color: rgb(137, 137, 137); font-family: Arial, &quot;Helvetica Neue&quot;, Helvetica, sans-serif; font-size: 14px; letter-spacing: 0.25px;\">&bull; Cải thảo c&oacute; m&agrave;u sắc kh&aacute; giống với cải bắp, phần l&aacute; bao ngo&agrave;i của m&agrave;u xanh đậm, c&ograve;n l&aacute; cuộn ở b&ecirc;n trong (gọi l&agrave; l&aacute; non) c&oacute; m&agrave;u xanh nhạt, trong khi phần cuống l&aacute; c&oacute; m&agrave;u trắng.&nbsp; C&Ocirc;NG DỤNG &bull; Cải thảo c&oacute; vị ngọt, t&iacute;nh m&aacute;t, c&oacute; t&aacute;c dụng hạ kh&iacute;, thanh nhiệt nhuận thấp, c&oacute; c&ocirc;ng dụng l&agrave;m mềm cổ họng,</span></p>\r\n', '', NULL, '2025-08-24 21:34:04', '2025-08-24 21:34:04');
INSERT INTO `products` (`id`, `name`, `category_id`, `image_url`, `price`, `slug`, `content`, `description`, `quantity`, `created_at`, `updated_at`) VALUES (5, ' Cải xanh Organic 300gr', 12, 'assets/uploads/68ab23052b4b6.png', 26000, 'cai-xanh-organic-300gr', '<p><span style=\"color: rgb(137, 137, 137); font-family: Arial, &quot;Helvetica Neue&quot;, Helvetica, sans-serif; font-size: 14px; letter-spacing: 0.25px;\">Cải bẹ xanh l&agrave; loại rau quen thuộc với gia đ&igrave;nh Việt, hay c&ograve;n gọi l&agrave; cải cay, l&agrave; th&agrave;nh phần chủ yếu trong m&ugrave; tạt (wasabi) v&agrave; thường được sử dụng trong c&aacute;c m&oacute;n hải sản tươi sống. Theo y học cổ truyền, cải bẹ xanh c&oacute; vị cay, t&iacute;nh &ocirc;n với t&aacute;c dụng thanh nhiệt, giải độc, giải cảm h&agrave;n, th&ocirc;ng đ&agrave;m, lợi kh&iacute;</span></p>\r\n', '', NULL, '2025-08-24 21:34:45', '2025-08-24 21:34:45');
INSERT INTO `products` (`id`, `name`, `category_id`, `image_url`, `price`, `slug`, `content`, `description`, `quantity`, `created_at`, `updated_at`) VALUES (6, 'Rau ngót Organic 300g', 12, 'assets/uploads/68ab2327d7d2b.png', 26000, 'rau-ngot-organic-300g', '<p><span style=\"color: rgb(137, 137, 137); font-family: Arial, &quot;Helvetica Neue&quot;, Helvetica, sans-serif; font-size: 14px; letter-spacing: 0.25px;\">C&aacute;c mặt h&agrave;ng tại Organic Mart lu&ocirc;n được ph&acirc;n chia với 3 ti&ecirc;u chuẩn r&otilde; r&agrave;ng tr&ecirc;n từng m&oacute;n h&agrave;ng. Chuẩn Organic (Chuẩn cao nhất) Chuẩn Global G.A.P (Chuẩn xuất khẩu) Chuẩn sạch tự nhi&ecirc;n (được trồng theo hướng hữu cơ hoặc tự nhi&ecirc;n 100% kh&ocirc;ng sử dụng thuốc v&agrave; chất h&oacute;a học trong khi trồng v&agrave; sau thu hoạch</span></p>\r\n', '', NULL, '2025-08-24 21:35:19', '2025-08-24 21:35:19');
INSERT INTO `products` (`id`, `name`, `category_id`, `image_url`, `price`, `slug`, `content`, `description`, `quantity`, `created_at`, `updated_at`) VALUES (7, ' Rau muống Organic 300gr', 12, 'assets/uploads/68ab234a8d786.png', 26000, 'rau-muong-organic-300gr', '<p><span style=\"color: rgb(137, 137, 137); font-family: Arial, &quot;Helvetica Neue&quot;, Helvetica, sans-serif; font-size: 14px; letter-spacing: 0.25px;\">M&ugrave;a rau muống l&agrave; cơ hội cho bạn thỏa sức chế biến những m&oacute;n ăn ngon, m&aacute;t l&agrave;nh, bổ dưỡng. Dưới đ&acirc;y l&agrave; v&agrave;i m&oacute;n cho bạn tham khảo. 1. Ốc m&oacute;ng tay x&agrave;o rau muống. Nguy&ecirc;n liệu: Rau muống, ốc m&oacute;ng tay, tỏi, gia vị. Ốc mua về ng&acirc;m nước cho &iacute;t muối v&agrave; ớt. Sau đ&oacute; lấy ốc ra trụng sơ với nước s&ocirc;i để bỏ vỏ. Th&ecirc;m</span></p>\r\n', '', NULL, '2025-08-24 21:35:54', '2025-08-24 21:35:54');
INSERT INTO `products` (`id`, `name`, `category_id`, `image_url`, `price`, `slug`, `content`, `description`, `quantity`, `created_at`, `updated_at`) VALUES (8, 'Rau cần tây Organic 300gr', 12, 'assets/uploads/68ab23784bedf.png', 26000, 'rau-can-tay-organic-300gr', '<p><span helvetica=\"\" neue=\"\" style=\"color: rgb(137, 137, 137); font-family: Arial, \">Cần t&acirc;y mang lại rất nhiều dưỡng chất thiết yếu cho cơ thể n&ecirc;n c&oacute; khả năng ph&ograve;ng chống một số bệnh nguy hiểm, đặc biệt l&agrave; chứng huyết &aacute;p cao. Kh&ocirc;ng chỉ l&agrave; nguy&ecirc;n liệu d&ugrave;ng để chế biến nhiều m&oacute;n ăn ngon, cần t&acirc;y c&ograve;n mang lại rất nhiều dưỡng chất thiết yếu cho cơ thể n&ecirc;n c&oacute; khả năng ph&ograve;ng chống một số</span></p>\r\n', 'Cần tây mang lại rất nhiều dưỡng chất thiết yếu cho cơ thể nên có khả năng phòng chống một số bệnh nguy hiểm, đặc biệt là chứng huyết áp cao. Không chỉ là nguyên liệu dùng để chế biến nhiều...', NULL, '2025-08-24 21:36:40', '2025-08-24 21:45:25');


#
# TABLE STRUCTURE FOR: settings
#

DROP TABLE IF EXISTS `settings`;

CREATE TABLE `settings` (
  `id` int NOT NULL AUTO_INCREMENT,
  `phone_number` varchar(45) DEFAULT NULL,
  `logo` varchar(255) DEFAULT NULL,
  `address` varchar(45) DEFAULT NULL,
  `email` varchar(45) DEFAULT NULL,
  `company_info` varchar(255) DEFAULT NULL,
  `fb_link` varchar(255) DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `company` varchar(45) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

