#
# TABLE STRUCTURE FOR: blogs
#

DROP TABLE IF EXISTS `blogs`;

CREATE TABLE `blogs` (
  `id` int NOT NULL AUTO_INCREMENT,
  `title` varchar(255) DEFAULT NULL,
  `slug` varchar(255) DEFAULT NULL,
  `description` text,
  `image_url` varchar(255) DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

INSERT INTO `blogs` (`id`, `title`, `slug`, `description`, `image_url`, `created_at`, `updated_at`) VALUES (1, 'Trang trí ban công', 'trang-tri-ban-cong', '<p helvetica=\"\" neue=\"\" style=\"font-family: \">Xin ch&agrave;o cả nh&agrave;, m&igrave;nh mới bố tr&iacute; lại ban c&ocirc;ng với lồng chim aviary, nh&igrave;n cũng thấy th&uacute; vị, chia sẻ c&ugrave;ng mọi người một số h&igrave;nh ảnh,</p>\r\n\r\n<p helvetica=\"\" neue=\"\" style=\"font-family: \">Mời c&aacute;c bạn v&agrave;o htxvannoi.com để ủng hộ m&igrave;nh nh&eacute;,</p>\r\n', 'assets/uploads/6899fbce6aa1b.jpg', '2025-08-11 21:18:29', '2025-08-11 21:18:54');
INSERT INTO `blogs` (`id`, `title`, `slug`, `description`, `image_url`, `created_at`, `updated_at`) VALUES (2, 'Cây mã đề', 'cay-ma-de', '<p><strong>M&ocirc; tả&nbsp;</strong>: C&acirc;y thảo sống l&acirc;u năm cao 15-20cm, c&oacute; th&acirc;n ngắn. L&aacute; mọc th&agrave;nh cụm ở gốc, c&oacute; cuống rộng ngắn hơn phiến, phiến h&igrave;nh th&igrave;a hay h&igrave;nh trứng, c&oacute; 2-3 g&acirc;n h&igrave;nh cung. Hoa nhỏ m&agrave;u trắng xếp th&agrave;nh b&ocirc;ng d&agrave;i, mọc đứng. Quả hộp nhỏ h&igrave;nh cầu, chứa 6-18 hạt. Hạt nhỏ trong hay bầu dục, to 1-1,5mm m&agrave;u đen b&oacute;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><img alt=\"\" src=\"https://htxvannoi.com/Anhtin/ma%20de.gif\" /></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Bộ phận d&ugrave;ng&nbsp;</strong>: Phần c&acirc;y tr&ecirc;n mặt -&nbsp;<em>Herba Plantaginis</em>, thường gọi l&agrave; Xa tiền thảo v&agrave; hạt -&nbsp;<em>Semen Plantaginis</em>, thường gọi l&agrave; Xa tiền tử.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Nơi sống v&agrave; thu h&aacute;i&nbsp;</strong>: C&acirc;y của v&ugrave;ng &Acirc;u&nbsp; &Aacute; &ocirc;n đới nay trở th&agrave;nh c&acirc;y to&agrave;n thế giới. Mọc hoang v&agrave; thường được trồng. Trồng bằng hạt nơi đất ẩm m&aacute;t v&agrave; m&ugrave;a xu&acirc;n. V&agrave;o th&aacute;ng 7-8, l&uacute;c quả ch&iacute;n, thu h&aacute;i to&agrave;n c&acirc;y đem về phơi kh&ocirc;, đập quả lấy hạt, rồi phơi kh&ocirc; cất d&agrave;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Th&agrave;nh phần ho&aacute; học&nbsp;</strong>: To&agrave;n c&acirc;y chứa một glucosid l&agrave; aucubin hay rinantin. L&aacute; c&oacute; chất nhầy, chất đắng, caroten, sinh tố C, K, T, acid citric. Trong hạt c&oacute; nhiều chất nhầy, acid plantenolic, adenin v&agrave; cholin.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>T&iacute;nh vị, t&aacute;c dụng&nbsp;</strong>: M&atilde; đề c&oacute; vị ngọt, t&iacute;nh m&aacute;t, c&oacute; t&aacute;c dụng ti&ecirc;u vi&ecirc;m, lợi tiểu, lợi phế, l&agrave;m long đờm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&ocirc;ng dụng&nbsp;</strong>: Thường d&ugrave;ng chữa: 1. Sỏi niệu v&agrave; nhiễm tr&ugrave;ng đường niệu, vi&ecirc;m thận, ph&ugrave; thũng; 2. Cảm lạnh ho, vi&ecirc;m kh&iacute; quản; 3. Vi&ecirc;m ruột, lỵ; 4. Vi&ecirc;m kết mạc cấp, vi&ecirc;m gan; 5. &ETH;au mắt đỏ c&oacute; m&agrave;ng. D&ugrave;ng to&agrave;n c&acirc;y 15-30g, hạt 5-10g dạng thuốc sắc. D&ugrave;ng ngo&agrave;i, gi&atilde; c&acirc;y tươi đắp mụn nhọt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ở Th&aacute;i Lan, to&agrave;n c&acirc;y được d&ugrave;ng lợi tiểu, trị vi&ecirc;m họng v&agrave; d&ugrave;ng ngo&agrave;i trị vi&ecirc;m mủ da, s&acirc;u bọ cắn v&agrave; dị ứng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&aacute;ch d&ugrave;ng&nbsp;</strong>: L&aacute; sắc hoặc nấu cao uống l&agrave;m thuốc th&ocirc;ng tiểu, chữa ho l&acirc;u ng&agrave;y, vi&ecirc;m kh&iacute; quản, tả lỵ, mắt đỏ đau. Cả c&acirc;y nấu cao đặc b&ocirc;i trị bỏng. (Lấy b&ocirc;ng nh&uacute;ng thuốc đắp l&ecirc;n chỗ bỏng băng lại, mỗi ng&agrave;y thay 1 lần). C&acirc;y tươi sắc nước uống chữa huyết &aacute;p cao. L&aacute; tươi sắc uống chữa lỵ cấp t&iacute;nh v&agrave; mạn t&iacute;nh. L&aacute; tươi gi&atilde; n&aacute;t đắp mụn nhọt, l&agrave;m mụn nhọt ch&oacute;ng vỡ mủ v&agrave; mau l&agrave;nh.</p>\r\n', 'assets/uploads/6899fbf733bd5.jpg', '2025-08-11 21:19:35', '2025-08-11 21:19:35');
INSERT INTO `blogs` (`id`, `title`, `slug`, `description`, `image_url`, `created_at`, `updated_at`) VALUES (3, 'Rau sạch - Hiểu thế nào là đúng', 'rau-sach---hieu-the-nao-la-dung', '<p><b helvetica=\"\" neue=\"\" style=\"font-family: \"><a href=\"https://htxvannoi.com/\" rel=\"nofollow\" style=\"color: rgb(51, 122, 183);\">Rau sạch</a></b><span helvetica=\"\" neue=\"\" style=\"font-family: \">&nbsp;trong suy nghĩ của đa số người ti&ecirc;u d&ugrave;ng chỉ đơn giản l&agrave; loại rau kh&ocirc;ng sử dụng hoa chất khi gieo trồng v&agrave; bảo quản. Tuy nhi&ecirc;n trong thực tế, để đ&aacute;nh gi&aacute;&nbsp;</span><b helvetica=\"\" neue=\"\" style=\"font-family: \">rau như thế n&agrave;o l&agrave; sạch</b><span helvetica=\"\" neue=\"\" style=\"font-family: \">&nbsp;th&igrave; phải dựa theo những quy tr&igrave;nh kỹ thuật đảm bảo được rất nhiều c&aacute;c ti&ecirc;u chuẩn kh&aacute;c nhau. C&ugrave;ng&nbsp;</span><b helvetica=\"\" neue=\"\" style=\"font-family: \"><a href=\"https://htxvannoi.com/\" rel=\"nofollow\" style=\"color: rgb(51, 122, 183);\">HTX V&acirc;n Nội</a></b><span helvetica=\"\" neue=\"\" style=\"font-family: \">&nbsp;đi t&igrave;m hiểu c&aacute;c quy tr&igrave;nh đ&oacute; l&agrave; g&igrave; nh&eacute;!</span></p>\r\n\r\n<div helvetica=\"\" neue=\"\" style=\"font-family: \">&nbsp;</div>\r\n\r\n<div helvetica=\"\" neue=\"\" style=\"font-family: \"><b>Rau sạch</b>&nbsp;hay c&ograve;n được gọi với c&aacute;i t&ecirc;n kh&aacute;c l&agrave;&nbsp;<b>rau an to&agrave;n</b>&nbsp;l&agrave; loại rau được sản xuất theo quy tr&igrave;nh kỹ thuật tu&acirc;n thủ c&aacute;c y&ecirc;u cầu như:</div>\r\n\r\n<h2 helvetica=\"\" neue=\"\" style=\"font-family: \">1. Đất trồng rau sạch</h2>\r\n\r\n<div helvetica=\"\" neue=\"\" style=\"font-family: \"><img alt=\"dat-trong-rau-sach-htx-van-noi.jpg\" border=\"0\" height=\"350\" src=\"https://htxvannoi.com/Anhtin/dat-trong-rau-sach-htx-van-noi.jpg\" title=\"Đất trồng rau sạch - HTX Vân Nội\" width=\"500\" /></div>\r\n\r\n<div helvetica=\"\" neue=\"\" style=\"font-family: \">&nbsp;</div>\r\n\r\n<div helvetica=\"\" neue=\"\" style=\"font-family: \">Đất phải sạch, chưa bị nhiễm c&aacute;c kim loại nặng (thủy ng&acirc;n, asen,...), nước thải c&ocirc;ng nghiệp (nếu v&ugrave;ng đất trồng gần c&aacute;c khu c&ocirc;ng nghiệp, nh&agrave; m&aacute;y sản xuất,...)</div>\r\n\r\n<div helvetica=\"\" neue=\"\" style=\"font-family: \">&nbsp;</div>\r\n\r\n<h2 helvetica=\"\" neue=\"\" style=\"font-family: \">2. Sử dụng ph&acirc;n b&oacute;n, thuốc trừ s&acirc;u đ&uacute;ng quy định</h2>\r\n\r\n<div helvetica=\"\" neue=\"\" style=\"font-family: \"><img alt=\"han-che-su-dung-phan-bon-thuoc-tru-sau-htx-van-noi.jpg\" border=\"0\" height=\"333\" src=\"https://htxvannoi.com/Anhtin/han-che-su-dung-phan-bon-thuoc-tru-sau-htx-van-noi.jpg\" title=\"Hạn chế sử dụng phân bón, thuốc trừ sâu - HTX Vân Nội\" width=\"500\" /></div>\r\n\r\n<div helvetica=\"\" neue=\"\" style=\"font-family: \">&nbsp;</div>\r\n\r\n<div helvetica=\"\" neue=\"\" style=\"font-family: \">Hạn chế mức thấp nhất việc sử dụng c&aacute;c loại ph&acirc;n b&oacute;n, thuốc trừ s&acirc;u, thuốc tăng trưởng,... để giảm tới mức thấp nhất lượng độc tố tồn đọng trong rau cũng như c&aacute;c vi sinh vật g&acirc;y bệnh.</div>\r\n\r\n<div helvetica=\"\" neue=\"\" style=\"font-family: \">\r\n<ul>\r\n	<li>Giảm lượng ph&acirc;n đạm b&oacute;n cho rau xanh v&igrave; ph&acirc;n đạm chưa nitrat g&acirc;y ảnh hưởng tới hoạt động của tuyến gi&aacute;p, giảm h&ocirc; hấp tế b&agrave;o, dẽ bị ngộ độc v&agrave; l&acirc;u dần sẽ g&acirc;y ung thư rất nguy hiểm. B&oacute;n ph&acirc;n h&oacute;a học đ&uacute;ng quy định, kết hợp với ph&acirc;n chuồng, ph&acirc;n xanh v&agrave; ph&acirc;n vi lượng l&agrave; biện ph&aacute;p l&agrave;m giảm nitrat trong rau.&nbsp;</li>\r\n	<li>N&ecirc;n sử dụng ph&acirc;n hữu cơ, ph&acirc;n chuồng hoai, mục để giảm c&aacute;c mầm bệnh, đặc biệt l&agrave; c&aacute;c vi sinh vật c&oacute; hại</li>\r\n	<li>Kh&ocirc;ng n&ecirc;n phun thuốc trừ s&acirc;u bởi ch&uacute;ng chứa nhiều gốc h&oacute;a học g&acirc;y hại cho cơ thể, l&agrave;m độc tổ tồn dư trong đất tăng, đặc biệt nếu ch&uacute;ng h&ograve;a tan v&agrave;o c&aacute;c nguồn nước sinh hoạt th&igrave; sẽ v&ocirc; c&ugrave;ng nguy hại. Nếu sử dụng th&igrave; phải hạn chế v&agrave; theo quy định.</li>\r\n	<li>Kh&ocirc;ng mua c&aacute;c loại thuốc kh&ocirc;ng r&otilde; nguồn gốc.</li>\r\n	<li>Hiện nay, việc sử dụng ph&acirc;n hữu cơ hoai, mục, ph&acirc;n vi sinh tổng hợp, ứng dụng c&aacute;c biện ph&aacute;p ph&ograve;ng trừ dịch bệnh tổng hợp (IPM) đối với sản xuất n&ocirc;ng nghiệp n&oacute;i chung v&agrave; với rau n&oacute;i ri&ecirc;ng đang được khuyến kh&iacute;ch.</li>\r\n	<li>Kh&ocirc;ng tưới rau bằng ph&acirc;n bắc, ph&acirc;n chuồng tươi, nước thải sinh hoạt, nước thải c&ocirc;ng nghiệp, c&aacute;c loại nước đ&atilde; bị nhiễm bẩn.</li>\r\n</ul>\r\n</div>\r\n\r\n<h2 helvetica=\"\" neue=\"\" style=\"font-family: \">3. Thời gian thu hoạch rau</h2>\r\n\r\n<div helvetica=\"\" neue=\"\" style=\"font-family: \"><img alt=\"thu-hoach-rau-sach-htx-van-noi.jpg\" border=\"0\" height=\"333\" src=\"https://htxvannoi.com/Anhtin/thu-hoach-rau-sach-htx-van-noi.jpg\" title=\"Thu hạch rau sạch - HTX Vân Nội\" width=\"500\" /></div>\r\n\r\n<div helvetica=\"\" neue=\"\" style=\"font-family: \">&nbsp;</div>\r\n\r\n<div helvetica=\"\" neue=\"\" style=\"font-family: \">Tuyệt đối kh&ocirc;ng thu hoạch ngay sau khi b&oacute;n ph&acirc;n hoặc mới phun thuốc. Phải đảm bảo bảo đủ thời gian ph&acirc;n hủy thuốc th&igrave; mới thu hạch v&agrave; mang b&aacute;n.</div>\r\n\r\n<div helvetica=\"\" neue=\"\" style=\"font-family: \">&nbsp;</div>\r\n\r\n<div helvetica=\"\" neue=\"\" style=\"font-family: \">Tr&ecirc;n đ&acirc;y l&agrave; nhưng quy tr&igrave;nh kỹ thuật để đ&aacute;nh gi&aacute;&nbsp;<b><a href=\"https://htxvannoi.com/\" rel=\"nofollow\" style=\"color: rgb(51, 122, 183);\">rau an to&agrave;n</a></b>. H&atilde;y l&agrave; nhưng người ti&ecirc;u d&ugrave;ng th&ocirc;ng th&aacute;i để bảo vệ sức khỏe cho cả gia đ&igrave;nh bạn nh&eacute;!</div>\r\n\r\n<div helvetica=\"\" neue=\"\" style=\"font-family: \">&nbsp;</div>\r\n\r\n<div helvetica=\"\" neue=\"\" style=\"font-family: \">Mời bạn tham khảo:&nbsp;<b><a href=\"https://htxvannoi.com/Tinchitiet.aspx?tieude=M%E1%BA%B9o%20hay%20l%E1%BB%B1a%20ch%E1%BB%8Dn%20rau%20an%20to%C3%A0n%20cho%20c%C3%A1c%20b%C3%A0%20n%E1%BB%99i%20tr%E1%BB%A3&amp;Id_New=14\" rel=\"nofollow\" style=\"color: rgb(51, 122, 183);\">Mẹo hay lựa chọn rau an to&agrave;n cho c&aacute;c b&agrave; nội trợ</a></b></div>\r\n', 'assets/uploads/6899fc2dc69ab.jpg', '2025-08-11 21:19:59', '2025-08-11 21:20:29');
INSERT INTO `blogs` (`id`, `title`, `slug`, `description`, `image_url`, `created_at`, `updated_at`) VALUES (4, '5 bước chọn rau để bữa cơm của bạn trở nên thực sự an toàn', '5-buoc-chon-rau-de-bua-com-cua-ban-tro-nen-thuc-su-an-toan', '<p>Thời điểm gi&aacute;p tết hiện nay, nhu cầu sử dụng thực phẩm tăng nhanh, đặc biệt l&agrave; c&aacute;c loại rau củ, quả. Ch&iacute;nh v&igrave; điều n&agrave;y m&agrave; một số nơi đ&atilde; sử dụng nhiều hơn c&aacute;c h&oacute;a chất k&iacute;ch th&iacute;ch, thuốc bảo vệ thực vật kh&ocirc;ng đ&uacute;ng quy định để h&agrave;ng rau, rủ nhanh lớn v&agrave; tr&ocirc;ng đẹp mắt hơn nhằm đ&aacute;nh lừa người ti&ecirc;u d&ugrave;ng.</p>\r\n\r\n<p><img alt=\"rau-an-toan-van-noi.jpg\" border=\"0\" src=\"https://htxvannoi.com/Anhtin/rau-an-toan-van-noi.jpg\" /></p>\r\n\r\n<p>Để tự bảo vệ sức khỏe của gia đ&igrave;nh m&igrave;nh, mỗi người ti&ecirc;u d&ugrave;ng h&atilde;y l&agrave; người những người mua h&agrave;ng th&ocirc;ng minh lựa chọn những loại&nbsp;<a href=\"https://htxvannoi.com/\" rel=\"nofollow\">rau an to&agrave;n</a>&nbsp;để sử dụng. Mời bạn đọc tham khảo v&agrave; n&ecirc;n thực hiện theo 5 bước hướng dẫn dưới đ&acirc;y của b&agrave; Nguyễn Thị Lệ Thoa,&nbsp;Trưởng ph&ograve;ng Quản l&yacute; an to&agrave;n thực phẩm thuộc Chi cục Bảo vệ thực vật TP.HCM, một chuy&ecirc;n gia về sức khỏe v&agrave; an to&agrave;n vệ sinh thực phậm tại Việt Nam chia sẻ nh&eacute;!</p>\r\n\r\n<h2>Bước 1. Nguồn gốc v&agrave; c&aacute;ch thức trưng b&agrave;y rau sạch</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Chọn rau c&oacute; r&otilde; nguồn gốc xuất xứ, c&aacute;c đơn vị sản xuất v&agrave; b&agrave;y b&aacute;n c&oacute; đủ giấy ph&eacute;p kinh doanh, chứng nhận đủ điệu kiện sản xuất rau an to&agrave;n.</p>\r\n\r\n<p><img alt=\"dong-goi-rau-sach-van-noi.jpg\" border=\"0\" src=\"https://htxvannoi.com/Anhtin/dong-goi-rau-sach-van-noi.jpg\" /></p>\r\n\r\n<p>- Chọn sản phẩm rau, củ c&oacute; đ&oacute;ng g&oacute;i bao b&igrave;, th&ugrave;ng chứa hợp vệ sinh v&agrave; c&oacute; gắn nh&atilde;n m&aacute;c, nơi sản xuất, địa chỉ cụ thể tr&ecirc;n từng sản phẩm.</p>\r\n\r\n<p>- Chọn rau phải tươi mới, kh&ocirc;ng dập n&aacute;t.</p>\r\n\r\n<h2>Bước 2: Lựa chọn rau theo m&ugrave;a, vụ</h2>\r\n\r\n<p>- Th&ocirc;ng thường v&agrave;o m&ugrave;a mưa, nguy cơ rau bị nhiễm h&oacute;a chất, thuốc bảo vệ thực vật sẽ &iacute;t hơn m&ugrave;a kh&ocirc;.</p>\r\n\r\n<p>- Chọn rau v&agrave;o ch&iacute;nh vụ, t&ugrave;y từng loại rau lại c&oacute; một m&ugrave;a vụ kh&aacute;c nhau, khi đ&oacute; rau sẽ ph&aacute;t triển mạnh nhất, &iacute;t s&acirc;u bệnh cũng như h&oacute;a chất v&agrave; ph&acirc;n b&oacute;n.</p>\r\n\r\n<h2>Bước 3: Lựa chọn rau sạch theo h&igrave;nh thức bề ngo&agrave;i</h2>\r\n\r\n<p>- Đối với rau ăn l&aacute;: Nếu m&agrave;u rau qu&aacute; xanh, l&aacute; qu&aacute; mướt, b&oacute;ng, c&acirc;y rau c&oacute; vẻ kh&ocirc;ng b&igrave;nh thường th&igrave; kh&ocirc;ng n&ecirc;n chọn.</p>\r\n\r\n<p>- Đối với rau ăn ngọn: Với những b&oacute; rau c&oacute; ngọn vươn qu&aacute; d&agrave;i, nếu bạn mua về m&agrave; kh&ocirc;ng sử dụng ngay, đến ng&agrave;y h&ocirc;m sau n&oacute; c&oacute; thể vươn th&ecirc;m 5-10cm th&igrave; cũng kh&ocirc;ng n&ecirc;n chọn.</p>\r\n\r\n<p>- Rau cải (cải ngọt, cải thảo,...): C&aacute;ch dễ nhất để ph&acirc;n biệt đ&acirc;u l&agrave; rau sạch th&igrave; bạn h&atilde;y bẻ ngang phần gốc. Nếu thấy c&oacute; nước từ th&acirc;n c&acirc;y tiết ra th&igrave; chứng tỏ rau đ&oacute; đ&atilde; bị b&oacute;n nhiều ph&acirc;n đạm, chưa đủ thời gian c&aacute;ch ly, h&agrave;m lượng Nitrat c&ograve;n cao. Để qu&aacute; 12h rau sẽ bị &uacute;ng chuyển m&agrave;u n&acirc;u đen.</p>\r\n\r\n<p>- Rau cần: Kh&ocirc;ng n&ecirc;n mua b&oacute; rau c&oacute; th&acirc;n to, ng&oacute; trắng ngần, rau sẽ nhanh bị h&eacute;o, để đến h&ocirc;m sau rau sẽ chuyển m&agrave;u xanh đen.</p>\r\n\r\n<p>- Củ, quả: Cần loại bỏ những tr&aacute;i qu&aacute; lớn, bề ngo&agrave;i b&oacute;ng nhẫy, &iacute;t l&ocirc;ng tơ, da căng v&agrave; c&oacute; vết nứt dọc theo th&acirc;n,...</p>\r\n\r\n<h2>Bước 4: Phải rửa rau thật sạch</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Nhặt l&aacute; &uacute;a, v&agrave;ng, l&aacute; bị s&acirc;u, cắt rễ, rửa sạch đất bẩn d&iacute;nh tr&ecirc;n c&acirc;y. Ng&acirc;m trong nước muối pha lo&atilde;ng trong thời gian từ 20-30 ph&uacute;t v&agrave; ng&acirc;m nhiều lần.</p>\r\n\r\n<p><img alt=\"nam-rau-bang-nuoc-muoi-HTX-van-noi.jpg\" border=\"0\" src=\"https://htxvannoi.com/Anhtin/nam-rau-bang-nuoc-muoi-HTX-van-noi.jpg\" /></p>\r\n\r\n<p>- Đối với những loại rau c&oacute; bẻ như cải thảo, cải bắp th&igrave; phải cắt bỏ gốc, t&aacute;ch rời từng l&aacute;. Phải rửa kỹ từng kẽ lẽ v&igrave; nơi đ&oacute; c&oacute; rất nhiều đất hoặc s&acirc;u bệnh dưới v&ograve;i nước &iacute;t nhất ba lần. Sau đ&oacute; ng&acirc;m nước muối pha lo&atilde;ng như tr&ecirc;n.</p>\r\n\r\n<p>- C&aacute;c loại củ: Phải rửa sạch, ng&acirc;m nước muối pha lo&atilde;ng, gọt vỏ v&agrave; rửa lại một lần nữa rồi mới chế biến.</p>\r\n\r\n<p>- C&aacute;c loại quả: Khi mua về kh&ocirc;ng n&ecirc;n ăn ngay, phải rửa sạch từng tr&aacute;i, ng&acirc;m kỹ nước muối pha lưỡng sau đ&oacute; bọc nilon, cất v&agrave;o trong tủ lạnh sau 2 ng&agrave;y mới n&ecirc;n ăn.</p>\r\n\r\n<h2>Bước 5: Chế biến rau</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- H&atilde;y sử dụng nhiều loại rau trong bữa ăn để tr&aacute;nh ngộ độc v&igrave; ăn qu&aacute; nhiều một loại rau (nếu bạn kh&ocirc;ng sơ chế kỹ) v&agrave; đảm bảo đầy đủ nhu cầu dinh dưỡng cho cơ thể.</p>\r\n\r\n<p><img alt=\"che-bien-rau-sach-htx-van-noi.jpg\" border=\"0\" src=\"https://htxvannoi.com/Anhtin/che-bien-rau-sach-htx-van-noi.jpg\" /></p>\r\n\r\n<p>- Khi chế biến, n&ecirc;n thỉnh thoảng mở nắp vung, nồi để cho hoa chất, thuốc trừ s&acirc;u (nếu c&oacute;) sẽ bay bớt ra b&ecirc;n ngo&agrave;i.</p>\r\n\r\n<p>Nếu bạn muốn c&oacute; một sức khỏe dồi d&agrave;o, c&aacute;c bữa cơm của gia đ&igrave;nh bạn sẽ an to&agrave;n hơn th&igrave; h&atilde;y lựa chọn ch&uacute;ng t&ocirc;i,&nbsp;<a href=\"https://htxvannoi.com/\" rel=\"nofollow\"><strong>HTX V&acirc;n Nội</strong></a>&nbsp;lu&ocirc;n cam kết cung cấp cho bạn c&aacute;c loại rau tuyệt đối an to&agrave;n 100%,&nbsp;n&oacute;i kh&ocirc;ng với thực phẩm bẩn,&nbsp;kh&ocirc;ng sử dụng thuốc bảo vệ thực vật,&nbsp;kh&ocirc;ng sử dụng h&oacute;a chất bảo quản. H&atilde;y tin tưởng ch&uacute;ng t&ocirc;i, bạn sẽ kh&ocirc;ng phải thất vọng. Gọi ngay&nbsp;<strong>0913 886 333</strong>&nbsp;để được tư vấn ngay h&ocirc;m nay c&aacute;c bạn nh&eacute;!</p>\r\n\r\n<p>Mời bạn tham khảo:&nbsp;<a href=\"https://htxvannoi.com/Tinchitiet.aspx?tieude=Rau%20s%E1%BA%A1ch%20-%20Hi%E1%BB%83u%20th%E1%BA%BF%20n%C3%A0o%20l%C3%A0%20%C4%91%C3%BAng&amp;Id_New=26\" rel=\"nofollow\"><strong>Rau sạch - Hiểu thế n&agrave;o l&agrave; đ&uacute;ng</strong></a></p>\r\n', 'assets/uploads/6899fc274d364.jpg', '2025-08-11 21:20:23', '2025-08-11 21:20:23');
INSERT INTO `blogs` (`id`, `title`, `slug`, `description`, `image_url`, `created_at`, `updated_at`) VALUES (5, 'Sốc - Tương lai có thể chúng ta phải uống sữa gián', 'soc---tuong-lai-co-the-chung-ta-phai-uong-sua-gian', '<p style=\"font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px;\">Mới đ&acirc;y, giới chuy&ecirc;n gia ph&aacute;t hiện ra,&nbsp;<a href=\"https://htxvannoi.com/timkiem.aspx?tk=rau%20s%E1%BA%A1ch\" rel=\"nofollow\" style=\"color: rgb(51, 122, 183);\">sữa</a>&nbsp;gi&aacute;n l&agrave; nguồn dinh dưỡng tiềm ẩn do chứa h&agrave;m lượng protein cao. Đ&acirc;y được coi l&agrave; nguồn&nbsp;<a href=\"https://htxvannoi.com/timkiem.aspx?tk=rau%20s%E1%BA%A1ch\" rel=\"nofollow\" style=\"color: rgb(51, 122, 183);\">thực phẩm</a>&nbsp;qu&yacute; gi&aacute; - gi&uacute;p hạ nhiệt cơn khủng hoảng&nbsp;<a href=\"https://htxvannoi.com/timkiem.aspx?tk=rau%20s%E1%BA%A1ch\" rel=\"nofollow\" style=\"color: rgb(51, 122, 183);\">lương thực</a>&nbsp;tr&ecirc;n to&agrave;n thế giới hiện nay.</p>\r\n\r\n<div style=\"font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px;\">\r\n<div><img alt=\"Tin sốc: Chúng ta có thể phải uống sữa gián trừ cơm trong tương lai - Ảnh 1.\" class=\"xulianh1\" src=\"http://kenh14cdn.com/2016/gian3-1469409627211.jpg\" title=\"Tin sốc: Chúng ta có thể phải uống sữa gián trừ cơm trong tương lai\" /></div>\r\n\r\n<div>\r\n<p>&nbsp;</p>\r\n</div>\r\n</div>\r\n\r\n<p style=\"font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px;\">Cụ thể, c&aacute;c nh&agrave; khoa học ph&aacute;t hiện dạ d&agrave;y của một lo&agrave;i gi&aacute;n đặc biệt - t&ecirc;n khoa học l&agrave; Diploptera punctata c&oacute; chứa tinh thể protein&nbsp;<a href=\"https://htxvannoi.com/timkiem.aspx?tk=rau%20s%E1%BA%A1ch\" rel=\"nofollow\" style=\"color: rgb(51, 122, 183);\">sữa</a>. Đặc biệt hơn, Diploptera punctata l&agrave; lo&agrave;i gi&aacute;n đẻ con - 1 h&igrave;nh thức sinh sản kh&aacute; kỳ lạ ở c&ocirc;n tr&ugrave;ng.</p>\r\n\r\n<p style=\"font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px;\">Giống như động vật c&oacute; v&uacute;, ch&uacute;ng d&ugrave;ng&nbsp;<a href=\"https://htxvannoi.com/timkiem.aspx?tk=rau%20s%E1%BA%A1ch\" rel=\"nofollow\" style=\"color: rgb(51, 122, 183);\">sữa</a>&nbsp;mẹ nu&ocirc;i con. V&agrave; đặc biệt hơn, nguồn dinh dưỡng trong sữa của lo&agrave;i gi&aacute;n n&agrave;y cao gấp 3 lần&nbsp;<a href=\"https://htxvannoi.com/timkiem.aspx?tk=rau%20s%E1%BA%A1ch\" rel=\"nofollow\" style=\"color: rgb(51, 122, 183);\">sữa b&ograve;</a>.</p>\r\n\r\n<div style=\"font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px;\">\r\n<div><img alt=\"Tin sốc: Chúng ta có thể phải uống sữa gián trừ cơm trong tương lai\" class=\"xulianh1\" src=\"http://kenh14cdn.com/2016/gian-1469409627204.jpg\" title=\"Tin sốc: Chúng ta có thể phải uống sữa gián trừ cơm trong tương lai\" /></div>\r\n\r\n<div>\r\n<p>Gi&aacute;n cảnh Diploptera punctata c&oacute; k&iacute;ch thước nhỏ, m&agrave;u n&acirc;u đỏ v&agrave; cực nhanh nhẹn. C&aacute;c loại tr&aacute;i c&acirc;y ngọt l&agrave; m&oacute;n ăn kho&aacute;i khẩu của ch&uacute;ng.</p>\r\n</div>\r\n</div>\r\n\r\n<p style=\"font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px;\">Tuy nhi&ecirc;n, vắt&nbsp;<a href=\"https://htxvannoi.com/timkiem.aspx?tk=rau%20s%E1%BA%A1ch\" rel=\"nofollow\" style=\"color: rgb(51, 122, 183);\">sữa gi&aacute;n</a>&nbsp;l&agrave; một c&ocirc;ng việc ho&agrave;n to&agrave;n kh&ocirc;ng dễ d&agrave;ng. Bởi vậy, một nh&oacute;m c&aacute;c nh&agrave; khoa học đang nghi&ecirc;n cứu ph&aacute;t triển phương thức rẻ, ph&ugrave; hợp để khai th&aacute;c vật phẩm qu&yacute; n&agrave;y.</p>\r\n\r\n<p style=\"font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px;\">Trong một nghi&ecirc;n cứu mới đ&acirc;y, nh&oacute;m chuy&ecirc;n gia đ&atilde; đưa dự &aacute;n trong việc sắp xếp gene sản xuất tinh thể protein sữa trong cơ thể gi&aacute;n. Nghi&ecirc;n cứu n&agrave;y được cho l&agrave; bước đột ph&aacute;, c&oacute; &yacute; nghĩa v&ocirc; c&ugrave;ng quan trọng khi n&oacute; gi&uacute;p giới khoa học tạo ra lượng lớn&nbsp;<a href=\"https://htxvannoi.com/timkiem.aspx?tk=rau%20s%E1%BA%A1ch\" rel=\"nofollow\" style=\"color: rgb(51, 122, 183);\">sản phẩm</a>&nbsp;gi&agrave;u dưỡng chất n&agrave;y.</p>\r\n\r\n<div style=\"font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px;\">\r\n<div><img alt=\"Tin sốc: Chúng ta có thể phải uống sữa gián trừ cơm trong tương lai\" class=\"xulianh1\" src=\"http://kenh14cdn.com/thumb_w/650/2016/gian1-1469409627231.jpg\" title=\"Tin sốc: Chúng ta có thể phải uống sữa gián trừ cơm trong tương lai\" /></div>\r\n\r\n<div>\r\n<p d=\"\">&nbsp;</p>\r\n</div>\r\n</div>\r\n\r\n<p style=\"font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px;\">Sanchari Banerjee - người tham gia nghi&ecirc;n cứu chia sẻ:&nbsp;&quot;C&aacute;c tinh thể sữa n&agrave;y giống như 1 loại thực phẩm ho&agrave;n chỉnh. Ch&uacute;ng c&oacute; chứa protein, chất b&eacute;o, đường v&agrave; cả c&aacute;c axit amin thiết yếu&quot;.&nbsp;<br />\r\nDẫu vậy, nh&oacute;m chuy&ecirc;n gia kh&ocirc;ng c&oacute; kế hoạch biến sữa gi&aacute;n th&agrave;nh sản phẩm thương mại mặc d&ugrave; họ đ&atilde; th&agrave;nh c&ocirc;ng trong việc sản xuất những tinh thể n&agrave;y.</p>\r\n', 'assets/uploads/6899fc4dbd188.jpg', '2025-08-11 21:21:01', '2025-08-11 21:21:01');
INSERT INTO `blogs` (`id`, `title`, `slug`, `description`, `image_url`, `created_at`, `updated_at`) VALUES (6, 'THU HOẠCH RAU CẢI XANH', 'thu-hoach-rau-cai-xanh', '<p><span style=\"font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px;\">Sau đợt mưa vừa rồi, bằng kĩ thuật th&acirc;m canh v&agrave; kinh nghiệm của 1 v&ugrave;ng trồng rau l&acirc;u năm ch&uacute;ng t&ocirc;i vẫn đảm bảo sản lượng mang đến cho thị trường đủ lượng rau.</span></p>\r\n\r\n<div style=\"font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px;\">Ở những v&ugrave;ng kh&aacute;c, do kĩ thuật sản xuất chưa tốt, cộng với thiếu kinh nghiệm n&ecirc;n ở một số v&ugrave;ng canh t&aacute;c rau m&agrave;u kh&aacute;c sau trận mưa b&atilde;o k&eacute;o d&agrave;i vừa rồi hầu như rau m&agrave;u đ&atilde; bị thiệt hại nặng nề, g&acirc;y ra sự leo thang gi&aacute; cả của tất cả c&aacute;c mặt h&agrave;ng rau củ quả.</div>\r\n\r\n<div style=\"font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px;\">Nhưng h&igrave;nh ảnh dưới đ&acirc;y cho thấy, htx rau an to&agrave;n x&atilde; V&acirc;n Nội ch&uacute;ng t&ocirc;i vẫn đảm bảo được lượng rau cho những kh&aacute;ch h&agrave;ng truyền thống của m&igrave;nh</div>\r\n\r\n<div style=\"font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px;\">&nbsp;</div>\r\n\r\n<div style=\"font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; text-align: center;\"><img alt=\"WP_20160904_15_55_32_Pro.jpg\" border=\"0\" src=\"https://htxvannoi.com/Anhtin/WP_20160904_15_55_32_Pro.jpg\" /></div>\r\n\r\n<div style=\"font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px;\">&nbsp;</div>\r\n\r\n<div style=\"font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px;\"><b>Ảnh : Thu hoạch rau cảnh xanh - Sử dụng&nbsp;<a href=\"https://www.thegioididong.com/welcome\" rel=\"nofollow\" style=\"color: rgb(51, 122, 183);\" target=\"_blank\">&ocirc;</a>&nbsp;v&agrave; d&ugrave;ng vải để che cho rau sau khi thu hoạch để rau tươi ngon</b></div>\r\n\r\n<div style=\"font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px;\">&nbsp;</div>\r\n\r\n<div style=\"font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px;\">Ngo&agrave;i kĩ thuật canh t&aacute;c tốt, kĩ thuật thu hoạch cũng l&agrave; một điều quan trọng để lưu giữ bảo quản rau được tươi ngon trước khi đưa tới tay người ti&ecirc;u d&ugrave;ng,</div>\r\n\r\n<div style=\"font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px;\">&nbsp;</div>\r\n\r\n<div style=\"font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px;\">Nguồn b&agrave;i viết :&nbsp;<a href=\"https://htxvannoi.com/tintuc/tinchitiet?id=25\" rel=\"nofollow\" style=\"color: rgb(51, 122, 183);\">HTX rau an to&agrave;n V&acirc;n Nội</a></div>\r\n', 'assets/uploads/6899fc6de2d41.jpg', '2025-08-11 21:21:33', '2025-08-11 21:21:33');


