INSERT INTO `categories` (`id`, `name`, `slug`, `type`, `content`, `created_at`, `updated_at`) VALUES (11, 'Giới thiệu htx', 'gioi-thieu', 3, '<p><b>&ldquo;HTX CB-SX-TT SPNN AN TO&Agrave;N V&Acirc;N NỘI&rdquo;</b>&nbsp;l&agrave; trung t&acirc;m n&ocirc;ng nghiệp ph&aacute;t triển theo hướng &aacute;p dụng c&ocirc;ng nghệ cao, v&agrave; những th&agrave;nh tựu khoa học v&agrave;o việc trồng v&agrave; canh t&aacute;c c&oacute; hiệu quả c&acirc;y trồng bao gồm c&aacute;c loại rau củ quả, c&acirc;y ăn tr&aacute;i đảm bảo mục ti&ecirc;u an to&agrave;n cho người ti&ecirc;u d&ugrave;ng, năng suất cao v&agrave; ổn định qua từng thời kỳ.</p>\r\n\r\n<p><b>&ldquo;Hợp t&aacute;c x&atilde; sản xuất ti&ecirc;u thụ chế biến sản phẩm n&ocirc;ng nghiệp an to&agrave;n x&atilde; V&acirc;n Nội&rdquo;&nbsp;</b>, với<span style=\"color: rgb(223, 31, 35);\"><b>&nbsp;tr&ecirc;n 20 năm</b></span>&nbsp;trong lĩnh vực sản xuất ti&ecirc;u thụ v&agrave; chế biến sản phẩm n&ocirc;ng nghiệp an to&agrave;n, ch&uacute;ng t&ocirc;i lu&ocirc;n muốn mang đến cho cuộc sống của c&aacute;c bạn những gi&aacute; trị dinh dưỡng cao nhất, một cuộc sống trong l&agrave;nh với những thực phẩm được trồng v&agrave; chăm s&oacute;c theo quy tr&igrave;nh chất lượng nghi&ecirc;m ngặt.</p>\r\n\r\n<p>Hiện nay, nhu cầu về sản phẩm rau an to&agrave;n ng&agrave;y c&agrave;ng tăng, nhất l&agrave; đối với người d&acirc;n trong khu vực nội th&agrave;nh. Trong khi thị trường với rất nhiều loại rau tr&aacute;i kh&ocirc;ng r&otilde; nguồn gốc xuất xứ, kh&ocirc;ng đảm bảo vệ sinh an to&agrave;n thực phẩm tr&agrave;n lan tr&ecirc;n thị trường g&acirc;y rất nhiều kh&oacute; khăn cho việc lựa chọn của người ti&ecirc;u d&ugrave;ng,&nbsp;<b>&ldquo;HTX sản xuất ti&ecirc;u thụ chế biến sản phẩm n&ocirc;ng nghiệp an to&agrave;n x&atilde; V&acirc;n Nội&rdquo;&nbsp;</b>được th&agrave;nh lập với mong muốn đưa tới tận tay người ti&ecirc;u d&ugrave;ng những sản phẩm rau tr&aacute;i an to&agrave;n.</p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"color: rgb(223, 31, 35);\"><b>Ch&uacute;ng t&ocirc;i, những người n&ocirc;ng d&acirc;n, những kĩ sư n&ocirc;ng nghiệp, lu&ocirc;n muốn l&agrave;m tốt nhất với tất cả khả năng của m&igrave;nh c&oacute; để mang lại những gi&aacute; trị tốt đẹp cho cuộc sống v&agrave; cho tương lai.</b></span></p>\r\n\r\n<p><b>Dưới đ&acirc;y l&agrave; giấy đăng k&yacute; kinh doanh v&agrave; Giấy Chứng nhận của ch&uacute;ng t&ocirc;i</b>&nbsp;<img alt=\"\" class=\"slide-image\" src=\"https://htxvannoi.com/Anh/Anhweb/DKKD1.jpg\" style=\"width: 847.5px;\" />&nbsp;<img alt=\"\" class=\"slide-image\" src=\"https://htxvannoi.com/Anh/Anhweb/DKKD2.jpg\" style=\"width: 847.5px;\" />&nbsp;<img alt=\"\" class=\"slide-image\" src=\"https://htxvannoi.com/Anh/Anhweb/DK%20Thue.jpg\" style=\"width: 847.5px;\" />&nbsp;<img alt=\"\" class=\"slide-image\" src=\"https://htxvannoi.com/Anh/Anhweb/Vietgap-images-0.jpg\" style=\"width: 847.5px;\" />&nbsp;<img alt=\"\" class=\"slide-image\" src=\"https://htxvannoi.com/Anh/Anhweb/Vietgap-images-1.jpg\" style=\"width: 847.5px;\" />&nbsp;<img alt=\"\" class=\"slide-image\" src=\"https://htxvannoi.com/Anh/Anhweb/Vietgap-images-2.jpg\" style=\"width: 847.5px;\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n', '2025-08-11 17:09:32', '2025-08-11 21:09:09');


